mapper.select:
SELECT video.VI_ID, video.VI_TITLE, video.VI_NAME, video.VI_PIC, video.V_SEC_ID, video.DURATION, manager.M_ID, manager.M_NICKNAME, manager.M_PIC FROM v_top_category top LEFT JOIN v_sec_category sec ON top.V_TOP_ID = sec.V_TOP_ID LEFT JOIN v_video_info video ON sec.V_SEC_ID = video.V_SEC_ID LEFT JOIN v_manager manager ON video.M_ID = manager.M_ID
top.V_TOP_ID = #{vTopId,jdbcType=DECIMAL}
AND sec.V_SEC_ID = #{vSecId,jdbcType=DECIMAL}
ORDER BY top.V_TOP_ID ASC,sec.V_SEC_ID ASC, video.VI_ID DESC limit #{startIndex},#{pageSize}

SELECT video.VI_ID, video.VI_TITLE, video.VI_NAME, video.VI_PIC, video.DURATION, video.V_SEC_ID, manager.M_ID, manager.M_NICKNAME, manager.M_PIC FROM v_top_category top LEFT JOIN v_sec_category sec ON top.V_TOP_ID = sec.V_TOP_ID LEFT JOIN v_video_info video ON sec.V_SEC_ID = video.V_SEC_ID LEFT JOIN v_manager manager ON video.M_ID = manager.M_ID
sec.V_SEC_ID = #{vSecId,jdbcType=DECIMAL}
OR video.VI_ID = #{vId,jdbcType=DECIMAL}
ORDER BY top.V_TOP_ID ASC,sec.V_SEC_ID ASC, video.VI_ID DESC limit #{startIndex},#{pageSize}

SELECT count(1) FROM v_top_category top LEFT JOIN v_sec_category sec ON top.V_TOP_ID = sec.V_TOP_ID LEFT JOIN v_video_info video ON sec.V_SEC_ID = video.V_SEC_ID LEFT JOIN v_manager manager ON video.M_ID = manager.M_ID
top.V_TOP_ID = #{vTopId,jdbcType=DECIMAL}
AND sec.V_SEC_ID = #{vSecId,jdbcType=DECIMAL}

SELECT video.VI_ID, video.VI_TITLE, video.VI_NAME, video.VI_PIC, video.DURATION, video.V_SEC_ID, manager.M_ID, manager.M_NICKNAME, manager.M_PIC FROM v_video_info video LEFT JOIN v_manager manager ON video.M_ID = manager.M_ID
video.VI_TITLE like '%${keywords}%' OR manager.M_NICKNAME like '%${keywords}%'
ORDER BY video.V_SEC_ID DESC, video.VI_ID DESC limit #{startIndex},#{pageSize}
